diff --git a/src/main/java/org/apache/commons/compress/archivers/ArchiveEntry.java b/src/main/java/org/apache/commons/compress/archivers/ArchiveEntry.java
index 0f8ed0878..4706dc33f 100644
--- a/src/main/java/org/apache/commons/compress/archivers/ArchiveEntry.java
+++ b/src/main/java/org/apache/commons/compress/archivers/ArchiveEntry.java
@@ -82,7 +82,12 @@ default Path resolveIn(final Path parentPath) throws IOException {
             throw new IOException(String.format("Zip slip '%s' + '%s' -> '%s'", parentPath, name, outputFile));
         }
         try {
-            return parentPath.resolve(URLDecoder.decode(name, StandardCharsets.US_ASCII.name()));
+            final String decodedName = URLDecoder.decode(name, StandardCharsets.US_ASCII.name());
+            final Path decodedOutputFile = parentPath.resolve(decodedName).normalize();
+            if (!decodedOutputFile.startsWith(parentPath)) {
+                throw new IOException(String.format("Zip slip '%s' + '%s' -> '%s'", parentPath, decodedName, decodedOutputFile));
+            }
+            return decodedOutputFile;
         } catch (IllegalArgumentException | UnsupportedEncodingException e) {
             return outputFile;
         }
