         }
     }

+    private static String sanitizeName(String name) {
+        if (name == null) {
+            return "null";
+        }
+        // Remove path traversal sequences and path separators
+        String sanitized = name.trim()
+            .replace("..", "_")
+            .replace("/", "_")
+            .replace("\\", "_")
+            .replace("\0", "_");
+
+        // Ensure the sanitized name is not empty
+        if (sanitized.isEmpty()) {
+            return "_";
+        }
+
+        return sanitized;
+    }
+
     public static void dump(DirectoryEntry root, File parent) throws IOException {
         for(Iterator<Entry> it = root.getEntries(); it.hasNext();){
             Entry entry = it.next();
@@ -105,12 +124,14 @@ public final class POIFSDump {
                 try (DocumentInputStream is = new DocumentInputStream(node)) {
                    bytes = IOUtils.toByteArray(is);
                 }
-                try (OutputStream out = Files.newOutputStream(new File(parent, node.getName().trim()).toPath())) {
+                String safeName = sanitizeName(node.getName());
+                try (OutputStream out = Files.newOutputStream(new File(parent, safeName).toPath())) {
                     out.write(bytes);
                 }
             } else if (entry instanceof DirectoryEntry){
                 DirectoryEntry dir = (DirectoryEntry)entry;
