 int
 exif_mnote_data_apple_identify(const ExifData *ed, const ExifEntry *e) {
-    int variant;
+    int variant = 0;
+    const char *apple_ios_str = "Apple iOS";
+    size_t apple_ios_len = 9; /* Length of "Apple iOS" */

-    if (!strcmp((const char *) e->data, "Apple iOS")) {
