+            !memcmp(e->data, apple_ios_str, apple_ios_len)) {
+            variant = 1;
+        }
     } else {
-        variant = 0;
+        /* Data is null-terminated, safe to use strcmp */
+        if (!strcmp((const char *) e->data, apple_ios_str)) {
+            variant = 1;
+        }
     }

     return variant;
