+    /**
+     * Validates that the resolved path does not escape the base directory.
+     * This prevents path traversal attacks using sequences like "../".
+     *
+     * @param basePath the base directory that output should be contained within
+     * @param resolvedPath the resolved output path to validate
+     * @throws TikaEmitterException if the path attempts to traverse outside the base directory
+     */
+    private void validatePath(Path basePath, Path resolvedPath) throws TikaEmitterException {
+        try {
+            Path normalizedBase = basePath.toRealPath();
+            Path normalizedResolved = resolvedPath.normalize().toAbsolutePath();
+
+            if (!normalizedResolved.startsWith(normalizedBase)) {
+                throw new TikaEmitterException("Path traversal detected: " + resolvedPath +
+                    " is outside base directory " + basePath);
+            }
+        } catch (IOException e) {
+            // If base path doesn't exist yet, use normalized absolute paths
+            Path normalizedBase = basePath.normalize().toAbsolutePath();
+            Path normalizedResolved = resolvedPath.normalize().toAbsolutePath();
+
+            if (!normalizedResolved.startsWith(normalizedBase)) {
+                throw new TikaEmitterException("Path traversal detected: " + resolvedPath +
+                    " is outside base directory " + basePath);
+            }
+        }
+    }
+
