diff --git a/src/main/java/org/apache/commons/compress/archivers/tar/TarUtils.java b/src/main/java/org/apache/commons/compress/archivers/tar/TarUtils.java
index 3b2b5be88..a5866d820 100644
--- a/src/main/java/org/apache/commons/compress/archivers/tar/TarUtils.java
+++ b/src/main/java/org/apache/commons/compress/archivers/tar/TarUtils.java
@@ -767,11 +767,17 @@ private static long[] readLineOfNumberForPax1X(final InputStream inputStream) th
         int number;
         long result = 0;
         long bytesRead = 0;
+        // Limit to prevent denial of service on malicious/corrupted archives.
+        // Maximum valid long (Long.MAX_VALUE) has 19 digits, plus newline = 20 bytes max.
+        final int maxBytesPerLine = 20;
         while ((number = inputStream.read()) != '\n') {
             bytesRead += 1;
             if (number == -1) {
                 throw new IOException("Unexpected EOF when reading parse information of 1.X PAX format");
             }
+            if (bytesRead > maxBytesPerLine) {
+                throw new IOException("Corrupted TAR archive. Number exceeds maximum length in sparse headers block");
+            }
             if (number < '0' || number > '9') {
                 throw new IOException("Corrupted TAR archive. Non-numeric value in sparse headers block");
             }
