diff:
diff --git a/epan/dissectors/packet-aim.c b/epan/dissectors/packet-aim.c
index ec79a79475..5450d5806c 100644
--- a/epan/dissectors/packet-aim.c
+++ b/epan/dissectors/packet-aim.c
@@ -587,8 +587,15 @@ aim_get_buddyname(wmem_allocator_t *pool, uint8_t **name, tvbuff_t *tvb, int off

 	buddyname_length = tvb_get_ntohs(tvb, offset);

+	/* Limit buddyname_length to the allocated buffer size to prevent buffer overflow */
+	if (buddyname_length > 255)
+		buddyname_length = 255;
+
 	if (pool)
-		memcpy(*name, tvb_get_ptr(tvb,offset,1), buddyname_length);
+		memcpy(*name, tvb_get_ptr(tvb,offset+2,buddyname_length), buddyname_length);
+
+	/* Null-terminate the string */
+	(*name)[buddyname_length] = '\0';

 	return buddyname_length;
 }
