diff --git a/lib/ftp.c b/lib/ftp.c
index cc5308791..806d04bb9 100644
--- a/lib/ftp.c
+++ b/lib/ftp.c
@@ -2095,6 +2095,10 @@ static CURLcode ftp_state_mdtm_resp(struct Curl_easy *data,
           return result;

         /* format: "Tue, 15 Nov 1994 12:45 and twenty-six seconds" */
+        /* Clamp tm_sec to 59 to handle leap seconds (tm_sec can be 60) */
+        int sec_idx = tm->tm_sec;
+        if(sec_idx < 0 || sec_idx > 59)
+          sec_idx = 59;
         headerbuflen =
           msnprintf(headerbuf, sizeof(headerbuf),
             "Last-Modified: %s, %02d %s %4d %02d:%02d and %s seconds GMT\r\n",
@@ -2104,7 +2108,7 @@ static CURLcode ftp_state_mdtm_resp(struct Curl_easy *data,
                     tm->tm_year + 1900,
                     tm->tm_hour,
                     tm->tm_min,
-                    Curl_seconds[tm->tm_sec]);
+                    Curl_seconds[sec_idx]);
         result = client_write_header(data, headerbuf, headerbuflen);
         if(result)
           return result;
