-    ether_output(ifp, hlen + plen);
-  }
+  // MG_DEBUG(("ICMP payload length %u", (unsigned) pkt->pay.len));
+  if (pkt->ip == NULL) return;
+  size_t eth_hlen = sizeof(struct eth);
+  size_t ihl = (pkt->ip->ver & 0xF) * 4;
+  size_t in_hlen = eth_hlen + ihl + sizeof(struct icmp);
+  // ensure full ICMP header present in received buffer
+  if (pkt->raw.len < in_hlen) return;
+  // clamp received payload length
+  size_t avail = pkt->raw.len - in_hlen, plen = pkt->pay.len;
+  if (plen > avail) plen = avail;
+  // only echo requests for this interface
+  if (pkt->icmp->type != 8 || pkt->ip->dst != ifp->ip) return;
+  // prepare echo reply transmit buffer
+  size_t out_hlen = eth_hlen + ihl + sizeof(struct icmp);
+  size_t space = ifp->tx.len - out_hlen;
+  if (plen > space) plen = space;
+  if (plen & 1) plen--;  // even length for checksum
+  struct ip *ip = tx_ip(ifp, pkt->eth->src, 1, ifp->ip, pkt->ip->src,
+                        sizeof(struct icmp) + plen);
+  struct icmp *icmp = (struct icmp *)(ip + 1);
+  memset(icmp, 0, sizeof(*icmp));
+  memcpy(icmp + 1, pkt->pay.buf, plen);
+  icmp->csum = ipcsum(icmp, sizeof(*icmp) + plen);
+  ether_output(ifp, out_hlen + plen);
 }

