+++ b/src/json.c
@@ -597,50 +597,59 @@

                         continue;

-                     case 't':
-
-                        if ((end - state.ptr) < 3 || *(++ state.ptr) != 'r' ||
-                            *(++ state.ptr) != 'u' || *(++ state.ptr) != 'e')
-                        {
-                           goto e_unknown_value;
-                        }
-
-                        if (!new_value (&state, &top, &root, &alloc, json_boolean))
-                           goto e_alloc_failure;
-
-                        top->u.boolean = 1;
-
-                        flags |= flag_next;
-                        break;
-
-                     case 'f':
-
-                        if ((end - state.ptr) < 4 || *(++ state.ptr) != 'a' ||
-                            *(++ state.ptr) != 'l' || *(++ state.ptr) != 's' ||
-                            *(++ state.ptr) != 'e')
-                        {
-                           goto e_unknown_value;
-                        }
-
-                        if (!new_value (&state, &top, &root, &alloc, json_boolean))
-                           goto e_alloc_failure;
-
-                        flags |= flag_next;
-                        break;
-
-                     case 'n':
-
-                        if ((end - state.ptr) < 3 || *(++ state.ptr) != 'u' ||
-                            *(++ state.ptr) != 'l' || *(++ state.ptr) != 'l')
-                        {
-                           goto e_unknown_value;
-                        }
-
-                        if (!new_value (&state, &top, &root, &alloc, json_null))
-                           goto e_alloc_failure;
-
-                        flags |= flag_next;
-                        break;
+    case 't':
+
+        /* match "true" */
+        if (end - state.ptr < 4 ||
+            *++state.ptr != 'r' ||
+            *++state.ptr != 'u' ||
+            *++state.ptr != 'e')
+        {
+            goto e_unknown_value;
+        }
+
+        if (!new_value(&state, &top, &root, &alloc, json_boolean))
+            goto e_alloc_failure;
+
+        top->u.boolean = 1;
+        flags |= flag_next;
+        break;
+
+    case 'f':
+
+        /* match "false" */
+        if (end - state.ptr < 5 ||
+            *++state.ptr != 'a' ||
+            *++state.ptr != 'l' ||
+            *++state.ptr != 's' ||
+            *++state.ptr != 'e')
+        {
+            goto e_unknown_value;
+        }
+
+        if (!new_value(&state, &top, &root, &alloc, json_boolean))
+            goto e_alloc_failure;
+
+        top->u.boolean = 0;
+        flags |= flag_next;
+        break;
+
+    case 'n':
+
+        /* match "null" */
+        if (end - state.ptr < 4 ||
+            *++state.ptr != 'u' ||
+            *++state.ptr != 'l' ||
+            *++state.ptr != 'l')
+        {
+            goto e_unknown_value;
+        }
+
+        if (!new_value(&state, &top, &root, &alloc, json_null))
+            goto e_alloc_failure;
+
+        flags |= flag_next;
+        break;

                      default:
