+static void
+free_tree(TreeNode *node, TreeNode *parent)
+{
+    if (!node)
+        return;
+
+    for (size_t i = 0; i < node->edge_count; ++i) {
+        TreeNode *child = node->edges[i];
+        if (child != parent)
+            free_tree(child, node);
+    }
+    free(node->edges);
+    free(node);
 }

