-        /*printf("%s(%d)\n", __FUNCTION__, __LINE__);*/
-        return;
-    }
-
-    tcount = (unsigned int) exif_get_short(buf + ofs + 14, d->order);
-
-    /* Sanity check the offset */
-    if (buf_size < 6 + 16 + tcount * 6 + 4) {
