+        if (tcount && needed / tcount != 12) {
+            exif_log(md->log,
+                     EXIF_LOG_CODE_CORRUPT_DATA,
+                     "ExifMnoteDataApple",
+                     "Too many MakerNote entries");
+            return;
+        }
+        if (header_end + needed > buf_size) {
+            exif_log(md->log,
+                     EXIF_LOG_CODE_CORRUPT_DATA,
+                     "ExifMnoteDataApple",
+                     "Short MakerNote");
+            return;
+        }
+    }
+
+    /* Advance past header and free old entries */
     ofs += 16;
