+                ofs += 12;
+                continue;
+            }
+            dofs = d->offset + rel;
         } else {
             dofs = ofs + 8;
         }
-	if (dofs > buf_size) {
-		exif_log (md->log, EXIF_LOG_CODE_CORRUPT_DATA,
