diff:
--- a/libexif/fuji/exif-mnote-data-fuji.c
+++ b/libexif/fuji/exif-mnote-data-fuji.c
@@ -172,10 +172,34 @@

 	n->order = EXIF_BYTE_ORDER_INTEL;

-	datao += exif_get_long (buf + datao + 8, EXIF_BYTE_ORDER_INTEL);
+	/* Safely read MakerNote pointer field */
+	{
+		uint32_t raw_ptr;
+		/* Validate pointer field location */
+		if (CHECKOVERFLOW(datao + 8, buf_size, 4)) {
+			exif_log(en->log, EXIF_LOG_CODE_CORRUPT_DATA,
+			         "ExifMnoteDataFuji",
+			         "MakerNote pointer field out of range (%u >= %u)",
+			         (unsigned)(datao + 8 + 4), buf_size);
+			return;
+		}
+		raw_ptr = exif_get_long(buf + datao + 8, EXIF_BYTE_ORDER_INTEL);
+		/* Validate pointer value against buffer bounds */
+		if (raw_ptr > buf_size - (6 + n->offset)) {
+			exif_log(en->log, EXIF_LOG_CODE_CORRUPT_DATA,
+			         "ExifMnoteDataFuji",
+			         "MakerNote pointer value out of range (%u >= %u)",
+			         raw_ptr, buf_size - (6 + n->offset));
+			return;
+		}
+		datao = raw_ptr + 6 + n->offset;
+	}
+	/* Ensure the entry count field is within buffer */
 	if (CHECKOVERFLOW(datao, buf_size, 2)) {
-		exif_log (en->log, EXIF_LOG_CODE_CORRUPT_DATA,
-			  "ExifMnoteDataFuji", "Short MakerNote");
+		exif_log(en->log, EXIF_LOG_CODE_CORRUPT_DATA,
+		         "ExifMnoteDataFuji",
+		         "Short MakerNote entry count past end of buffer (%u >= %u)",
+		         (unsigned)(datao + 2), buf_size);
 		return;
 	}

@@ -238,15 +262,33 @@
 		s = exif_format_get_size (n->entries[tcount].format) * n->entries[tcount].components;
 		n->entries[tcount].size = s;
 		if (s) {
-			size_t dataofs = o + 8;
-			if (s > 4)
+			size_t dataofs;
+			if (s > 4) {
 				/* The data in this case is merely a pointer */
-				dataofs = exif_get_long (buf + dataofs, n->order) + 6 + n->offset;
-
+				uint32_t raw_ptr;
+				/* Ensure pointer field is within buffer */
+				if (CHECKOVERFLOW(o + 8, buf_size, 4)) {
+					exif_log(en->log, EXIF_LOG_CODE_CORRUPT_DATA,
+							 "ExifMnoteDataFuji",
+							 "MakerNote pointer out of range (%u >= %u)",
+							 (unsigned)(o + 8 + 4), buf_size);
+					continue;
+				}
+				raw_ptr = exif_get_long(buf + o + 8, n->order);
+				/* Ensure pointer value plus header fits */
+				if (raw_ptr > buf_size - (6 + n->offset)) {
+					exif_log(en->log, EXIF_LOG_CODE_CORRUPT_DATA,
+							 "ExifMnoteDataFuji",
+							 "MakerNote pointer value out of range (%u >= %u)",
+							 raw_ptr, buf_size - (6 + n->offset));
+					continue;
+				}
+				dataofs = raw_ptr + 6 + n->offset;
+			} else {
+				dataofs = o + 8;
+			}
+			/* Ensure the entire tag data resides in the buffer */
 			if (CHECKOVERFLOW(dataofs, buf_size, s)) {
-				exif_log (en->log, EXIF_LOG_CODE_CORRUPT_DATA,
-						  "ExifMnoteDataFuji", "Tag data past end of "
-					  "buffer (%u >= %u)", (unsigned)(dataofs + s), buf_size);
 				continue;
 			}
