diff:
--- a/encoding.c
+++ b/encoding.c
@@ -2847,7 +2847,8 @@
     const unsigned char *inend = in + (*inlen & ~1);
     unsigned char *outstart = out;
     unsigned long int b0, b1, b2, b3, c, d;
-    int ret = XML_ENC_ERR_SPACE;
+        int ret = XML_ENC_ERR_SPACE;
+        int initial_outlen = *outlen;

     if ((in[0] != 0x20) && (in[1] != 0x20) && (in[2] != 0x20) && (in[3] != 0x20)) {
         /* bom is managed already */
@@ -2865,18 +2866,21 @@
         c = b0 | b1 | b2 | b3;

         if (c < 0x80) {
+            if ((out - outstart) + 1 > initial_outlen) return XML_ENC_ERR_SPACE;
             out[0] = c;
             in += 4;
             out += 1;
         } else if (c < 0x800) {
-            out[0] = (c >> 6)   | 0xC0;
+            if ((out - outstart) + 2 > initial_outlen) return XML_ENC_ERR_SPACE;
+            out[0] = (c >> 6) | 0xC0;
             out[1] = (c & 0x3F) | 0x80;
             in += 4;
             out += 2;
         } else if ((c & 0xF800) != 0xD800) {
-            out[0] =  (c >> 12)         | 0xE0;
-            out[1] = ((c >>  6) & 0x3F) | 0x80;
-            out[2] =  (c        & 0x3F) | 0x80;
+            if ((out - outstart) + 3 > initial_outlen) return XML_ENC_ERR_SPACE;
+            out[0] = (c >> 12) | 0xE0;
+            out[1] = (((c >> 6) & 0x3F) | 0x80);
+            out[2] = ((c & 0x3F) | 0x80);
             in += 4;
             out += 3;
         } else {
@@ -2901,12 +2905,13 @@
                 out[2] = ((c >>  6) & 0x3F) | 0x80;
                 out[3] =  (c        & 0x3F) | 0x80;
             */
-            out[0] = c & 0xff;
-            out[1] = (c >> 8) & 0xff;
-            out[2] = (c >> 0x10) & 0xff;
-            out[3] = (c >> 0x18) & 0xff;
-            in += 4;
-            out += 4;
+        if ((out - outstart) + 4 > initial_outlen) return XML_ENC_ERR_SPACE;
+        out[0] = c & 0xff;
+        out[1] = (c >> 8) & 0xff;
+        out[2] = (c >> 0x10) & 0xff;
+        out[3] = (c >> 0x18) & 0xff;
+        in += 4;
+        out += 4;
         }
     }
