+        int avail = remaining();
+        // advance through CONTINUE records if no data
+        while (avail == 0 && hasNextRecord() && getNextSid() == ContinueRecord.sid) {
+            nextRecord();
+            avail = remaining();
+        }
+        if (avail == 0) {
+            return -1;
+        }
+        int toRead = Math.min(len, avail);
+        readFully(b, off, toRead);
+        return toRead;
+    }
