diff:
--- a/epan/dissectors/packet-icmp.c
+++ b/epan/dissectors/packet-icmp.c
@@ -923,14 +923,28 @@

 	switch(c_type) {
 		case ICMP_EXT_ECHO_IDENT_NAME:
-			/* Identification Name must be printable*/
-			tvb_memcpy(tvb, ident_name, offset, obj_length - 4);
-
-			for (unsigned i = 0; i < obj_length - 4; i++)
-				if (!g_ascii_isprint(ident_name[i]))
-					break;
-
-			proto_tree_add_item(ext_object_tree, hf_icmp_int_ident_name_string, tvb, offset, obj_length - 4, ENC_ASCII);
+			/* Identification Name must be printable */
+			{
+				/* Clamp to reported data and buffer size */
+				guint obj_trunc_length = MIN(obj_length, tvb_reported_length_remaining(tvb, offset));
+				guint data_len = obj_trunc_length > 4 ? obj_trunc_length - 4 : 0;
+				guint name_len = MIN(data_len, G_N_ELEMENTS(ident_name));
+
+				if (name_len > 0) {
+					tvb_memcpy(tvb, ident_name, offset, name_len);
+					for (guint i = 0; i < name_len; i++) {
+						if (!g_ascii_isprint(ident_name[i])) {
+							break;
+						}
+					}
+				}
+				/* Display the full (truncated) name length from the packet */
+				proto_tree_add_item(ext_object_tree,
+				                    hf_icmp_int_ident_name_string,
+				                    tvb, offset,
+				                    data_len,
+				                    ENC_ASCII);
+			}
 			break;
 		case ICMP_EXT_ECHO_IDENT_INDEX:
 			proto_tree_add_item(ext_object_tree, hf_icmp_int_ident_index, tvb, offset, 4, ENC_NA);
