+++ b/src/cmspack.c
@@ -98,6 +98,14 @@
                                   CMSREGISTER cmsUInt8Number* accum,
                                   CMSREGISTER cmsUInt32Number Stride)
 {
+    // Special-case named-color index: 1 channel of 2 bytes per sample
+    if ((info -> InputFormat & (CHANNELS_SH(1)|BYTES_SH(2))) == TYPE_NAMED_COLOR_INDEX) {
+        // Read big-endian 16-bit index
+        cmsUInt16Number idx = (cmsUInt16Number)((accum[0] << 8) | accum[1]);
+        wIn[0] = idx;
+        return accum + Stride;
+    }
+
     cmsUInt32Number nChan      = T_CHANNELS(info -> InputFormat);
     cmsUInt32Number DoSwap     = T_DOSWAP(info ->InputFormat);
     cmsUInt32Number Reverse    = T_FLAVOR(info ->InputFormat);
