diff:
--- a/encoding.c
+++ b/encoding.c
@@ -2848,7 +2848,8 @@
     unsigned char *outstart = out;
     unsigned long int b0, b1, b2, b3, c, d;
     int ret = XML_ENC_ERR_SPACE;
-
+    int maxOut = *outlen;
+    unsigned char *outend = out + maxOut;
     if ((in[0] != 0x20) && (in[1] != 0x20) && (in[2] != 0x20) && (in[3] != 0x20)) {
         /* bom is managed already */
     }
@@ -2865,15 +2866,18 @@
         c = b0 | b1 | b2 | b3;

         if (c < 0x80) {
+            if ((size_t)(outend - out) < 1) break;
             out[0] = c;
             in += 4;
             out += 1;
         } else if (c < 0x800) {
+            if ((size_t)(outend - out) < 2) break;
             out[0] = (c >> 6)   | 0xC0;
             out[1] = (c & 0x3F) | 0x80;
             in += 4;
             out += 2;
         } else if ((c & 0xF800) != 0xD800) {
+            if ((size_t)(outend - out) < 3) break;
             out[0] =  (c >> 12)         | 0xE0;
             out[1] = ((c >>  6) & 0x3F) | 0x80;
             out[2] =  (c        & 0x3F) | 0x80;
@@ -2901,6 +2905,7 @@
                 out[2] = ((c >>  6) & 0x3F) | 0x80;
                 out[3] =  (c        & 0x3F) | 0x80;
             */
+            if ((size_t)(outend - out) < 4) break;
             out[0] = c & 0xff;
             out[1] = (c >> 8) & 0xff;
             out[2] = (c >> 0x10) & 0xff;
