diff:
--- a/xmpbox/src/main/java/org/apache/xmpbox/xml/DomXmpParser.java
+++ b/xmpbox/src/main/java/org/apache/xmpbox/xml/DomXmpParser.java
@@ -79,6 +79,17 @@
         try
         {
             DocumentBuilderFactory dbFactory = DocumentBuilderFactory.newInstance();
+            try {
+                // Prevent XXE and SSRF via external entities and DTDs
+                dbFactory.setFeature(XMLConstants.FEATURE_SECURE_PROCESSING, true);
+                dbFactory.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
+                dbFactory.setFeature("http://xml.org/sax/features/external-general-entities", false);
+                dbFactory.setFeature("http://xml.org/sax/features/external-parameter-entities", false);
+                dbFactory.setAttribute(XMLConstants.ACCESS_EXTERNAL_DTD, "");
+                dbFactory.setAttribute(XMLConstants.ACCESS_EXTERNAL_SCHEMA, "");
+            } catch (ParserConfigurationException | IllegalArgumentException e) {
+                throw new XmpParsingException(ErrorType.Configuration, "Failed to configure secure XML parser", e);
+            }
             dbFactory.setNamespaceAware(true);
             dBuilder = dbFactory.newDocumentBuilder();
             nsFinder = new NamespaceFinder();
