@@ -542,7 +542,7 @@
     //     i += 1;
     // }

-    validate_message((uint8_t *)tvb_get_ptr(tvb, offset, body_len), val->str, body_len);
+    validate_message((uint8_t *)tvb_get_ptr(tvb, offset, body_len), *val->str, body_len);
     *offset += body_len;
     return val;
 }
