diff:
--- a/src/main/java/org/apache/commons/compress/archivers/ArchiveEntry.java
+++ b/src/main/java/org/apache/commons/compress/archivers/ArchiveEntry.java
@@ -74,11 +74,32 @@
      */
     default Path resolveIn(final Path parentPath) throws IOException {
         final String name = getName();
-        final Path outputFile = parentPath.resolve(name).normalize();
-        if (!outputFile.startsWith(parentPath)) {
-            throw new IOException(String.format("Zip slip '%s' + '%s' -> '%s'", parentPath, name, outputFile));
+        // Sanitize entry name to use forward slashes
+        String sanitized = name.replace('\\', '/');
+        // Reject absolute paths and Windows drive-letter prefixes
+        if (sanitized.startsWith("/") || sanitized.matches("^[A-Za-z]:.*")) {
+            throw new IOException("Illegal entry name: " + name);
         }
-        return outputFile;
+        Path result = parentPath;
+        for (String segment : sanitized.split("/")) {
+            if (segment.isEmpty() || ".".equals(segment)) {
+                continue;
+            }
+            if ("..".equals(segment)) {
+                if (result.equals(parentPath)) {
+                    throw new IOException("Entry escapes target directory: " + name);
+                }
+                result = result.getParent();
+            } else {
+                result = result.resolve(segment);
+            }
+        }
+        // Final normalization and safety check
+        result = result.normalize();
+        if (!result.startsWith(parentPath)) {
+            throw new IOException(String.format("Zip slip '%s' + '%s' -> '%s'", parentPath, name, result));
+        }
+        return result;
     }

 }

--- a/src/main/java/org/apache/commons/compress/archivers/examples/Expander.java
