--- a/libexif/apple/exif-mnote-data-apple.c
+++ b/libexif/apple/exif-mnote-data-apple.c
@@ -235,15 +235,19 @@

 int
 exif_mnote_data_apple_identify(const ExifData *ed, const ExifEntry *e) {
-    int variant;
-
-    if (!strcmp((const char *) e->data, "Apple iOS")) {
-        variant = 1;
-    } else {
-        variant = 0;
-    }
-
-    return variant;
+    const char apple_prefix[] = "Apple iOS";
+    const size_t prefix_len = sizeof(apple_prefix) - 1;
+
+    /* Guard against NULL pointers and too-short data */
+    if (!e || !e->data || e->size < prefix_len) {
+        return 0;
+    }
+
+    /* Safe comparison: only compare prefix_len bytes */
+    if (memcmp(e->data, apple_prefix, prefix_len) == 0) {
+        return 1;
+    }
+    return 0;
 }

 ExifMnoteData *
