diff:
--- a/encoding.c
+++ b/encoding.c
@@ -2846,6 +2846,7 @@
     const unsigned char *instart = in;
     const unsigned char *inend = in + (*inlen & ~1);
     unsigned char *outstart = out;
+    unsigned char *outend = out + *outlen;
     unsigned long int b0, b1, b2, b3, c, d;
     int ret = XML_ENC_ERR_SPACE;

@@ -2865,15 +2866,24 @@
         c = b0 | b1 | b2 | b3;

         if (c < 0x80) {
+            if (outend - out < 1) {
+                break;
+            }
             out[0] = c;
             in += 4;
             out += 1;
         } else if (c < 0x800) {
+            if (outend - out < 2) {
+                break;
+            }
             out[0] = (c >> 6)   | 0xC0;
             out[1] = (c & 0x3F) | 0x80;
             in += 4;
             out += 2;
         } else if ((c & 0xF800) != 0xD800) {
+            if (outend - out < 3) {
+                break;
+            }
             out[0] =  (c >> 12)         | 0xE0;
             out[1] = ((c >>  6) & 0x3F) | 0x80;
             out[2] =  (c        & 0x3F) | 0x80;
@@ -2885,26 +2895,22 @@
                 ret = XML_ERR_INTERNAL_ERROR;
                 break;
             }
-	        if (inend - in < 4)
-		        break;
+            if (inend - in < 4)
+                break;
             d = in[2] | (in[3] << 8);
             if ((d & 0xFC00) != 0xDC00) {
                 ret = XML_ERR_INTERNAL_ERROR;
                 break;
             }
-            /*
-                simplified due to encoding errors encountered with:
-
-                c = (c << 10) + d - ((0xD800 << 10) + 0xDC00 - 0x10000);
-                out[0] =  (c >> 18)         | 0xF0;
-                out[1] = ((c >> 12) & 0x3F) | 0x80;
-                out[2] = ((c >>  6) & 0x3F) | 0x80;
-                out[3] =  (c        & 0x3F) | 0x80;
-            */
-            out[0] = c & 0xff;
-            out[1] = (c >> 8) & 0xff;
-            out[2] = (c >> 0x10) & 0xff;
-            out[3] = (c >> 0x18) & 0xff;
+            /* compute actual code point */
+            c = (((c & 0x3FF) << 10) | (d & 0x3FF)) + 0x10000;
+            if (outend - out < 4) {
+                break;
+            }
+            out[0] = (c >> 18)        | 0xF0;
+            out[1] = ((c >> 12) & 0x3F)| 0x80;
+            out[2] = ((c >>  6) & 0x3F)| 0x80;
+            out[3] =  (c        & 0x3F)| 0x80;
             in += 4;
             out += 4;
         }
