 Condition* condition_free_list_type(Condition *head, ConditionType type) {
-        LIST_FOREACH(conditions, c, head)
-                if (type < 0 || c->type == type) {
+        /* Free all conditions if negative type */
+        if (type < 0) {
+                LIST_CLEAR(conditions, head, condition_free);
+                return NULL;
+        }
+
+        /* Safely traverse and remove only matching type */
+        LIST_FOREACH_WITH_NEXT(conditions, c, n, head) {
+                if (c->type == type) {
+                        LIST_REMOVE(conditions, head, c);
                         condition_free(c);
                 }
-
-        assert(type >= 0 || !head);
+        }
+
         return head;
 }
