diff:
--- a/epan/dissectors/packet-ber.c
+++ b/epan/dissectors/packet-ber.c
@@ -3174,9 +3174,10 @@
 dissect_ber_GeneralString(asn1_ctx_t *actx, proto_tree *tree, tvbuff_t *tvb, int offset, int hf_id, char *name_string, unsigned name_len)
 {
     tvbuff_t *out_tvb = NULL;
-    int8_t  tvb_len;
-
-    offset = dissect_ber_restricted_string(false, BER_UNI_TAG_GeneralString, actx, tree, tvb, offset, hf_id, (name_string) ? &out_tvb : NULL);
+
+    offset = dissect_ber_restricted_string(false, BER_UNI_TAG_GeneralString,
+                                           actx, tree, tvb, offset, hf_id,
+                                           (name_string) ? &out_tvb : NULL);

     if (name_string) {
         /*
@@ -3185,13 +3186,13 @@
          * do we want to throw an exception?
          */
         if (out_tvb) {
-            tvb_len = tvb_get_uint8(tvb, offset);
-            if ((unsigned)tvb_len >= name_len) {
-                tvb_memcpy(out_tvb, (uint8_t*)name_string, 0, name_len-1);
-            } else {
-                tvb_memcpy(out_tvb, (uint8_t*)name_string, 0, tvb_len);
-            }
-            name_string[tvb_len] = '\0';
+            /* Read the remaining string length as unsigned */
+            guint8 raw_len = tvb_get_uint8(tvb, offset);
+            /* Clamp to avoid overflow/underflow; reserve space for NUL */
+            guint copy_len = raw_len < (guint)name_len ? raw_len : (guint)name_len - 1;
+            /* Copy and NUL-terminate safely */
+            tvb_memcpy(out_tvb, (uint8_t*)name_string, 0, copy_len);
+            name_string[copy_len] = '\0';
         }
     }
