     private boolean prettyPrint = false;

+    /**
+     * Validates that the resolved path is a descendant of basePath and does not contain
+     * malicious patterns that could lead to path traversal attacks.
+     * 
+     * @param path the relative path to validate and resolve
+     * @return the validated resolved path
+     * @throws TikaEmitterException if validation fails
+     */
+    private Path validateAndResolve(String path) throws TikaEmitterException {
+        // Basic validation
+        if (path == null || path.isEmpty()) {
+            throw new TikaEmitterException("path must not be null or empty");
+        }
+        
+        // Prevent null byte injection
+        if (path.contains("\u0000")) {
+            throw new TikaEmitterException("path must not contain null bytes");
+        }
+        
+        // Prevent absolute path injection
+        Path pathObj = Paths.get(path);
+        if (pathObj.isAbsolute()) {
+            throw new TikaEmitterException("path must not be absolute: " + path);
+        }
+        
+        // Resolve and validate against path traversal
+        Path target = basePath.resolve(path);
+        Path normalizedTarget = target.toAbsolutePath().normalize();
+        Path normalizedBase = basePath.toAbsolutePath().normalize();
+        
+        if (!normalizedTarget.startsWith(normalizedBase)) {
+            throw new TikaEmitterException(
+                "path must not escape basePath. Attempted path: " + path);
+        }
+        
+        return target;
+    }
+
