diff:
--- a/epan/dissectors/packet-icmp.c
+++ b/epan/dissectors/packet-icmp.c
@@ -897,7 +897,7 @@
 				     proto_item * tf_object)
 {
 	proto_item *ti;
-	uint16_t obj_length;
+	uint16_t obj_length, obj_trunc_length;
 	uint8_t c_type;
 	uint8_t ident_name[ICMP_MAX_IDENT_NAME_LENGTH];
 	bool unknown_object;
@@ -908,6 +908,8 @@
 	/* Object length */
 	obj_length = tvb_get_ntohs(tvb, offset);

+	obj_trunc_length = MIN(obj_length, tvb_reported_length_remaining(tvb, offset));
+
 	/* C-Type */
 	c_type = tvb_get_uint8(tvb, offset + 3);

@@ -924,13 +926,20 @@
 	switch(c_type) {
 		case ICMP_EXT_ECHO_IDENT_NAME:
 			/* Identification Name must be printable*/
-			tvb_memcpy(tvb, ident_name, offset, obj_length - 4);
-
-			for (unsigned i = 0; i < obj_length - 4; i++)
-				if (!g_ascii_isprint(ident_name[i]))
-					break;
-
-			proto_tree_add_item(ext_object_tree, hf_icmp_int_ident_name_string, tvb, offset, obj_length - 4, ENC_ASCII);
+			if (obj_trunc_length >= 4) {
+				uint32_t payload_length = obj_trunc_length - 4;
+				uint32_t copy_length = MIN(payload_length, ICMP_MAX_IDENT_NAME_LENGTH);
+
+				if (copy_length > 0) {
+					tvb_memcpy(tvb, ident_name, offset, copy_length);
+
+					for (unsigned i = 0; i < copy_length; i++)
+						if (!g_ascii_isprint(ident_name[i]))
+							break;
+				}
+
+				proto_tree_add_item(ext_object_tree, hf_icmp_int_ident_name_string, tvb, offset, payload_length, ENC_ASCII);
+			}
 			break;
 		case ICMP_EXT_ECHO_IDENT_INDEX:
 			proto_tree_add_item(ext_object_tree, hf_icmp_int_ident_index, tvb, offset, 4, ENC_NA);
