+                    try {
+                        Path target = extractDir.resolve(tae.getName()).normalize();
+                        
+                        // Validate that the resolved path is within the extraction directory
+                        if (!target.toAbsolutePath().normalize().startsWith(extractDir.toAbsolutePath().normalize())) {
+                            LOG.warn("Skipping entry outside target directory: {}", tae.getName());
+                            tae = tais.getNextEntry();
+                            continue;
+                        }
+                        
+                        if (!Files.isDirectory(target.getParent())) {
+                            Files.createDirectories(target.getParent());
+                        }
+                        Files.write(target, tais.readAllBytes());
