diff:
--- a/encoding.c
+++ b/encoding.c
@@ -2844,12 +2844,13 @@
               const unsigned char *in, int *inlen,
               void *vctxt ATTRIBUTE_UNUSED) {
     const unsigned char *instart = in;
-    const unsigned char *inend = in + (*inlen & ~1);
+    const unsigned char *inend = in + (*inlen & ~3);
     unsigned char *outstart = out;
+    unsigned char *outend = out + *outlen;
     unsigned long int b0, b1, b2, b3, c, d;
     int ret = XML_ENC_ERR_SPACE;

-    if ((in[0] != 0x20) && (in[1] != 0x20) && (in[2] != 0x20) && (in[3] != 0x20)) {
+    if ((*inlen >= 4) && (in[0] != 0x20) && (in[1] != 0x20) && (in[2] != 0x20) && (in[3] != 0x20)) {
         /* bom is managed already */
     }

@@ -2865,15 +2866,21 @@
         c = b0 | b1 | b2 | b3;

         if (c < 0x80) {
+            if (out >= outend)
+                break;
             out[0] = c;
             in += 4;
             out += 1;
         } else if (c < 0x800) {
+            if (outend - out < 2)
+                break;
             out[0] = (c >> 6)   | 0xC0;
             out[1] = (c & 0x3F) | 0x80;
             in += 4;
             out += 2;
         } else if ((c & 0xF800) != 0xD800) {
+            if (outend - out < 3)
+                break;
             out[0] =  (c >> 12)         | 0xE0;
             out[1] = ((c >>  6) & 0x3F) | 0x80;
             out[2] =  (c        & 0x3F) | 0x80;
@@ -2882,30 +2889,24 @@
         } else {
             /* Surrogate pair */
             if ((c & 0xFC00) != 0xD800) {
-                ret = XML_ERR_INTERNAL_ERROR;
+                ret = XML_ENC_ERR_INPUT;
                 break;
             }
-	        if (inend - in < 4)
+	        if (inend - in < 8)
 		        break;
-            d = in[2] | (in[3] << 8);
+            d = in[4] | (in[5] << 8) | (in[6] << 16) | (in[7] << 24);
             if ((d & 0xFC00) != 0xDC00) {
-                ret = XML_ERR_INTERNAL_ERROR;
+                ret = XML_ENC_ERR_INPUT;
                 break;
             }
-            /*
-                simplified due to encoding errors encountered with:
-
-                c = (c << 10) + d - ((0xD800 << 10) + 0xDC00 - 0x10000);
-                out[0] =  (c >> 18)         | 0xF0;
-                out[1] = ((c >> 12) & 0x3F) | 0x80;
-                out[2] = ((c >>  6) & 0x3F) | 0x80;
-                out[3] =  (c        & 0x3F) | 0x80;
-            */
-            out[0] = c & 0xff;
-            out[1] = (c >> 8) & 0xff;
-            out[2] = (c >> 0x10) & 0xff;
-            out[3] = (c >> 0x18) & 0xff;
-            in += 4;
+            if (outend - out < 4)
+                break;
+            c = (c << 10) + d - ((0xD800 << 10) + 0xDC00 - 0x10000);
+            out[0] =  (c >> 18)         | 0xF0;
+            out[1] = ((c >> 12) & 0x3F) | 0x80;
+            out[2] = ((c >>  6) & 0x3F) | 0x80;
+            out[3] =  (c        & 0x3F) | 0x80;
+            in += 8;
             out += 4;
         }
     }
