diff:
--- a/encoding.c
+++ b/encoding.c
@@ -2844,8 +2844,9 @@
               const unsigned char *in, int *inlen,
               void *vctxt ATTRIBUTE_UNUSED) {
     const unsigned char *instart = in;
-    const unsigned char *inend = in + (*inlen & ~1);
+    const unsigned char *inend = in + (*inlen & ~3);
     unsigned char *outstart = out;
+    unsigned char *outend = out + *outlen;
     unsigned long int b0, b1, b2, b3, c, d;
     int ret = XML_ENC_ERR_SPACE;

@@ -2865,15 +2866,21 @@
         c = b0 | b1 | b2 | b3;

         if (c < 0x80) {
+            if (out >= outend)
+                goto done;
             out[0] = c;
             in += 4;
             out += 1;
         } else if (c < 0x800) {
+            if (outend - out < 2)
+                goto done;
             out[0] = (c >> 6)   | 0xC0;
             out[1] = (c & 0x3F) | 0x80;
             in += 4;
             out += 2;
         } else if ((c & 0xF800) != 0xD800) {
+            if (outend - out < 3)
+                goto done;
             out[0] =  (c >> 12)         | 0xE0;
             out[1] = ((c >>  6) & 0x3F) | 0x80;
             out[2] =  (c        & 0x3F) | 0x80;
@@ -2901,6 +2908,8 @@
                 out[2] = ((c >>  6) & 0x3F) | 0x80;
                 out[3] =  (c        & 0x3F) | 0x80;
             */
+            if (outend - out < 4)
+                goto done;
             out[0] = c & 0xff;
             out[1] = (c >> 8) & 0xff;
             out[2] = (c >> 0x10) & 0xff;
@@ -2910,9 +2919,9 @@
         }
     }

-    if (in-instart > 0)
-        ret = out - outstart;
-
+    ret = out - outstart;
+
+done:
     *outlen = out - outstart;
     *inlen = in - instart;
     return(ret);
