diff:
--- a/libexif/apple/exif-mnote-data-apple.c
+++ b/libexif/apple/exif-mnote-data-apple.c
@@ -28,6 +28,8 @@
 #include <libexif/exif-data.h>
 #include <libexif/exif-utils.h>

+#define CHECKOVERFLOW(offset,datasize,structsize) (( offset >= datasize) || (structsize > datasize) || (offset > datasize - structsize ))
+
 static void
 exif_mnote_data_apple_free(ExifMnoteData *md) {
     ExifMnoteDataApple *d = (ExifMnoteDataApple *) md;
@@ -59,6 +61,7 @@
     unsigned int tcount, i;
     unsigned int dsize;
     unsigned int ofs, dofs;
+    unsigned int tcount_parsed;

     /*printf("%s\n", __FUNCTION__);*/

@@ -82,10 +85,24 @@
         return;
     }

+    /* Verify we can read the tag count */
+    if (CHECKOVERFLOW(ofs + 14, buf_size, 2)) {
+        exif_log(md->log, EXIF_LOG_CODE_CORRUPT_DATA,
+                 "ExifMnoteDataApple", "Short MakerNote");
+        return;
+    }
+
     tcount = (unsigned int) exif_get_short(buf + ofs + 14, d->order);

-    /* Sanity check the offset */
-    if (buf_size < 6 + 16 + tcount * 6 + 4) {
+    /* Limit tag count to prevent resource exhaustion */
+    if (tcount > 100) {
+        exif_log(md->log, EXIF_LOG_CODE_CORRUPT_DATA,
+                 "ExifMnoteDataApple", "Too many tags (%u) in Apple MakerNote", tcount);
+        return;
+    }
+
+    /* Sanity check that all entries fit in the buffer */
+    if (CHECKOVERFLOW(ofs + 16, buf_size, tcount * 12)) {
         exif_log(md->log, EXIF_LOG_CODE_CORRUPT_DATA,
                  "ExifMnoteDataApple", "Short MakerNote");
         /*printf("%s(%d)\n", __FUNCTION__, __LINE__);*/
@@ -106,32 +123,62 @@
         return;
     }

+    tcount_parsed = 0;
     for (i = 0; i < tcount; i++) {
-        d->entries[i].tag = exif_get_short(buf + ofs, d->order);
-        d->entries[i].format = exif_get_short(buf + ofs + 2, d->order);
-        d->entries[i].components = exif_get_long(buf + ofs + 4, d->order);
-        d->entries[i].order = d->order;
-        dsize = exif_format_get_size(d->entries[i].format) * d->entries[i].components;
+        /* Ensure 12 bytes are available for this entry */
+        if (CHECKOVERFLOW(ofs, buf_size, 12)) {
+            exif_log(md->log, EXIF_LOG_CODE_CORRUPT_DATA,
+                     "ExifMnoteDataApple", "Short MakerNote");
+            break;
+        }
+
+        d->entries[tcount_parsed].tag = exif_get_short(buf + ofs, d->order);
+        d->entries[tcount_parsed].format = exif_get_short(buf + ofs + 2, d->order);
+        d->entries[tcount_parsed].components = exif_get_long(buf + ofs + 4, d->order);
+        d->entries[tcount_parsed].order = d->order;
+
+        dsize = exif_format_get_size(d->entries[tcount_parsed].format) * d->entries[tcount_parsed].components;
+
+        /* Check for integer overflow in size calculation */
+        if (exif_format_get_size(d->entries[tcount_parsed].format) &&
+            buf_size / exif_format_get_size(d->entries[tcount_parsed].format) < d->entries[tcount_parsed].components) {
+            exif_log(md->log, EXIF_LOG_CODE_CORRUPT_DATA,
+                     "ExifMnoteDataApple", "Tag size overflow detected (%u * %u)",
+                     exif_format_get_size(d->entries[tcount_parsed].format), d->entries[tcount_parsed].components);
+            ofs += 12;
+            continue;
+        }
+
         if (dsize > 4) {
             dofs = d->offset + exif_get_long(buf + ofs + 8, d->order);
         } else {
             dofs = ofs + 8;
         }
-	if (dofs > buf_size) {
-		exif_log (md->log, EXIF_LOG_CODE_CORRUPT_DATA,
-                                  "ExifMnoteApplet", "Tag size overflow detected (%u vs size %u)", dofs, buf_size);
-		continue;
-	}
+
+        /* Verify the data location and size are within bounds */
+        if (CHECKOVERFLOW(dofs, buf_size, dsize)) {
+            exif_log(md->log, EXIF_LOG_CODE_CORRUPT_DATA,
+                     "ExifMnoteDataApple", "Tag data past end of buffer (%u > %u)",
+                     (unsigned)(dofs + dsize), buf_size);
+            ofs += 12;
+            continue;
+        }
+
         ofs += 12;
-        d->entries[i].data = exif_mem_alloc(md->mem, dsize);
-        if (!d->entries[i].data) {
+
+        d->entries[tcount_parsed].data = exif_mem_alloc(md->mem, dsize);
+        if (!d->entries[tcount_parsed].data) {
             EXIF_LOG_NO_MEMORY(md->log, "ExifMnoteApple", dsize);
             continue;
         }
-        memcpy(d->entries[i].data, buf + dofs, dsize);
-        d->entries[i].size = dsize;
-    }
-    d->count = tcount;
+
+        memcpy(d->entries[tcount_parsed].data, buf + dofs, dsize);
+        d->entries[tcount_parsed].size = dsize;
+
+        /* Entry successfully parsed */
+        tcount_parsed++;
+    }
+    d->count = tcount_parsed;

     return;
 }
