+            // Skip Java object deserialization for security reasons (RCE vulnerability)
+            // Record that Java serialized object data was encountered but do not deserialize
+            metadata.set(RTFMetadata.EMB_CLASS, "application/x-java-serialized-object");
+            metadata.add(TikaCoreProperties.TIKA_META_EXCEPTION_WARNING,
+                    "Java object deserialization skipped for security reasons. " +
+                    "Embedded Java serialized objects in RTF documents are not processed to prevent " +
+                    "potential remote code execution vulnerabilities.");
+            metadata.set(TikaCoreProperties.CONTENT_TYPE_PARSER_OVERRIDE,
+                    MediaType.text("plain").toString());
+            // Extract empty content instead of deserializing the object
+            extractObj(new byte[0], handler, metadata);
         } else if (state == EMB_STATE.NADA) {
