     @Override
     public void emit(String path, InputStream inputStream, Metadata userMetadata, ParseContext parseContext) throws IOException, TikaEmitterException {
-        Path target = basePath.resolve(path);
+        // Normalize and validate the supplied path to prevent traversal
+        java.nio.file.Path relativePath = java.nio.file.Paths.get(path);
+        if (relativePath.isAbsolute()) {
+            throw new TikaEmitterException("Absolute paths not allowed: " + path);
+        }
+        java.nio.file.Path normalizedRelative = relativePath.normalize();
+        for (java.nio.file.Path segment : normalizedRelative) {
+            if (segment.toString().equals("..")) {
+                throw new TikaEmitterException("Path traversal detected in: " + path);
+            }
+        }
+        java.nio.file.Path target = basePath.resolve(normalizedRelative).normalize();
+        if (!target.startsWith(basePath)) {
+            throw new TikaEmitterException("Resolved path outside output directory: " + target);
+        }

         if (!Files.isDirectory(target.getParent())) {
             Files.createDirectories(target.getParent());
