diff:
--- a/libfreerdp/codec/rfx.c
+++ b/libfreerdp/codec/rfx.c
@@ -139,7 +139,7 @@
 	PROFILER_PRINT_FOOTER
 }

-UINT64 metadataBlock = (UINT64)&explicit_bzero;
+/* Removed global metadataBlock; metadata handling is now skipped entirely */

 static INLINE void rfx_tile_init(void* obj)
 {
@@ -176,7 +176,7 @@
 	return tile;
 }

-UINT64 metadataData = 0;
+/* Removed global metadataData; metadata handling is now skipped entirely */

 static INLINE void rfx_decoder_tile_free(void* obj)
 {
@@ -671,13 +671,10 @@
 	Stream_Read_UINT32(
 	    s, frameIdx); /* frameIdx (4 bytes), if codec is in video mode, must be ignored */
 	Stream_Read_UINT16(s, numRegions); /* numRegions (2 bytes) */
-	if (Stream_GetRemainingLength(s) >= 8)
-	{
-	        UINT64 blockOffset = 0;
-	        Stream_Read_UINT64(s, blockOffset);
-	        Stream_Read_UINT64(s, metadataData);
-	        metadataBlock += blockOffset;
-	}
+	/* Skip legacy metadata blockOffset and metadataData fields */
+	if (!Stream_CheckAndLogRequiredLengthWLog(context->priv->log, s, 16))
+	    return FALSE;
+	Stream_SafeSeek(s, 16);
 	WLog_Print(context->priv->log, WLOG_DEBUG,
 	           "RFX_FRAME_BEGIN: frameIdx: %" PRIu32 " numRegions: %" PRIu16 "", frameIdx,
 	           numRegions);
@@ -725,13 +722,11 @@
 		return FALSE;
 	}

-	Stream_Read_UINT32(s, magic); /* magic (4 bytes), 0xCACCACCA */
-	if (magic != WF_MAGIC)
-	{
-		WLog_Print(context->priv->log, WLOG_ERROR, "invalid magic number 0x%08" PRIX32 "", magic);
-		return FALSE;
-	}
-	*(UINT64*)metadataBlock = metadataData;
+	/* Skip metadata block entirely without memory writes */
+	if (!Stream_CheckAndLogRequiredLengthWLog(context->priv->log, s, 12))
+	    return FALSE;
+	/* 4 bytes magic + 8 bytes metadataData */
+	Stream_SafeSeek(s, 12);
 	return TRUE;
 }
