diff:
--- a/src/main/java/org/apache/commons/compress/archivers/ArchiveEntry.java
+++ b/src/main/java/org/apache/commons/compress/archivers/ArchiveEntry.java
@@ -72,13 +72,19 @@
      * @throws IOException if this method detects a Zip slip.
      * @since 1.26.0
      */
-    default Path resolveIn(final Path parentPath) throws IOException {
-        final String name = getName();
-        final Path outputFile = parentPath.resolve(name);
-        if (!outputFile.startsWith(parentPath)) {
-            throw new IOException(String.format("Zip slip '%s' + '%s' -> '%s'", parentPath, name, outputFile));
-        }
-        return outputFile;
+default Path resolveIn(final Path parentPath) throws IOException {
+    Path normalizedParent = parentPath.toAbsolutePath().normalize();
+    String name = getName();
+    if (name.startsWith("/") || name.startsWith("\\")
+            || name.contains("../") || name.contains("..\\")) {
+        throw new IOException(String.format("Invalid entry name '%s' leads to path traversal", name));
     }
+    Path resolved = normalizedParent.resolve(name).normalize();
+    if (!resolved.startsWith(normalizedParent)) {
+        throw new IOException(String.format("Zip slip '%s' + '%s' -> '%s'",
+                normalizedParent, name, resolved));
+    }
+    return resolved;
+}

 }
