     private Token readCharString(int length) throws IOException
     {
+        // limit charstring size to prevent excessive allocation
+        final int MAX_CHARSTRING_SIZE = 1 * 1024 * 1024; // 1 MiB
         try
         {
             buffer.get(); // space
+            // sanity-check length
+            if (length < 0 || length > buffer.remaining() || length > MAX_CHARSTRING_SIZE) {
+                throw new DamagedFontException("Invalid CharString length: " + length);
+            }
             byte[] data = new byte[length];
             buffer.get(data);
             return new Token(data, Token.CHARSTRING);
